import {useState} from 'react'
import {ReactComponent as Favorite} from '@material-design-icons/svg/filled/favorite.svg'
import './index.css'

export const UserRating = () => {
  const [count, setCount] = useState(0)

  const handlePlusClick = () => {
    if (count >= 5) {
      return 
    }
    setCount(count + 1)
  }

  const handleMinusClick = () => {
    if (count <= 0) {
      return
    }
    setCount(count - 1)
  }

  return (
    <div className="rating">
      <div className="controls">
        <div className="slot">
          {count > 0 ? (
            <button className="btn" onClick={handleMinusClick}>[-]</button>
          ) : (
            <span className="slot-spacer" />
          )}
        </div>

        <div className="hearts">
          {[...Array(count)].map((_, index) => (
            <span className="heart" key={index}>
              <Favorite />
            </span>
          ))}
        </div>

        <div className="slot">
          {count < 5 ? (
            <button className="btn" onClick={handlePlusClick}>[+]</button>
          ) : (
            <span className="slot-spacer" />
          )}
        </div>
      </div>
    </div>
  )
}

export default UserRating
