import {useState} from 'react'
import {ReactComponent as Favorite} from '@material-design-icons/svg/filled/favorite.svg'
import './index.css'

export const UserRating = () => {
  const [count, setCount] = useState(0)

  const handlePlusClick = () => {
    if (count >= 5) {
      return 
    }
    setCount(count + 1)
  }

  const handleMinusClick = () => {
    if (count <= 0) {
      return
    }
    setCount(count - 1)
  }

  return (
    <div className="ur-row">
      <button className="ur-btn" onClick={handleMinusClick} disabled={count <= 0}>[-]</button>

      <div className="ur-hearts">
        {[...Array(count)].map((i, index) => {
          return (
            <span key={index}>
              <Favorite />
            </span>
          )
        })}
        {count === 0 && <span className="ur-empty">No hearts yet</span>}
      </div>

      <button className="ur-btn" onClick={handlePlusClick} disabled={count >= 5}>[+]</button>
    </div>
  )
}

export default UserRating
